import React, { useEffect } from "react";
import { Grid, Button, createStyles, makeStyles, Theme, TextField } from "@material-ui/core";
import SaveIcon from '@material-ui/icons/Save';
import CustomSelect from "../CustomSelect";
import useMyStore from "../../zustand";
import { useForm } from "react-hook-form";
import CustomTextField from "../CustomTextField";

const [objStore] = useMyStore;
const useStyles = makeStyles((theme: Theme) =>
createStyles({
  button: {
    margin: theme.spacing(1),
  },
}),
);

type Inputs = {
    example: string,
    exampleRequired: string,
  };

const AddModal :  React.FC<{}> = () => {
    const classes = useStyles();



    const arrayActivity= objStore(state => state.activity);
    const getActivity=objStore(state => state.getActivity);
    const arrayType= objStore(state => state.type);
    const getType=objStore(state => state.getType);
    const isLoading = objStore(state =>state.isLoading);
    ;
    useEffect(() => {

        (async function (){
            await getActivity();
            await getType();
        })()
    }, 
    []
    );



    const renderActivity =():undefined| JSX.Element | string=>{
       if(arrayActivity && arrayActivity.contenido.length){
           return <CustomSelect    key={1}{...arrayActivity}  />;
       }else if (isLoading){
           return "Cargando";
       }
  }
        const renderType =():undefined| JSX.Element | string=>{
            if(arrayType && arrayType.contenido.length){
                return <CustomSelect key={1}{...arrayType}/>;
            }else if (isLoading){
                return "Cargando";
            }
        }


        const onSubmit = (data: any) => console.log(data);




    return (

    <form >

      <Grid container spacing={2}>
        <Grid item xs={12}>
          imagen
        </Grid>
        <Grid item xs={4}>
          <CustomTextField/>
        </Grid>
        <Grid item xs={4}>
         {renderActivity()  }
        </Grid>
        <Grid item xs={4}>
        {renderType()}
        </Grid>

        <Grid item xs={4}>

        </Grid>
        <Grid item xs={4}>
          combo1
        </Grid>
        <Grid item xs={4}>
          combo 2
        </Grid>

        <Grid item xs={4}>
          nombre
        </Grid>
        <Grid item xs={4}>
          combo1
        </Grid>
        <Grid item xs={4}>
          combo 2
        </Grid>
        <Grid item xs={12}>
        <Button
        type="submit"
        variant="contained"
        color="primary"
        size="large"
        className={classes.button}
        startIcon={<SaveIcon />}
      >
        Save
      </Button>
        </Grid>
        </Grid>
        </form>
    );


}

export default AddModal;
