import React, { useState, useEffect } from "react";
import style from '../../assets/css/App.styles'  

import ReactMapGL, { Marker, Popup } from "react-map-gl";
import * as parkDate from './../../data/skateboard-park.json';
    const MapCustom :  React.FC<{}> = () => {
      const [viewport, setViewport] = useState({
        latitude: 45.4211,
        longitude: -75.6903,
        width: "100vw",
        height: "100vh",
        zoom: 10
      });
      const [selectedPark, setSelectedPark] = useState(null);

      useEffect(() => {
        const listener = (e: { key: string; }) => {
          if (e.key === "Escape") {
            setSelectedPark(null);
          }
        };
        window.addEventListener("keydown", listener);

        return () => {
          window.removeEventListener("keydown", listener);
        };
      }, []);

            return (
              <div>
              <ReactMapGL
                {...viewport}
                mapboxApiAccessToken="sk.eyJ1IjoiZ3VpbGxlcm1vYW5kYSIsImEiOiJja2JiaHVjajYwMWY3MnJxdjgxanc1dTE0In0.IbWhbMXX85mY0Q3158lw1w"
                mapStyle="mapbox://styles/leighhalliday/cjufmjn1r2kic1fl9wxg7u1l4"
                onViewportChange={viewport => {
                  //setViewport(viewport);
                }}
              >
                {parkDate.features.map(park => (
                  <Marker
                    key={park.properties.PARK_ID}
                    latitude={park.geometry.coordinates[1]}
                    longitude={park.geometry.coordinates[0]}
                  >
                    <button
                      className="marker-btn"
                      onClick={e => {
                        e.preventDefault();

                      }}
                    >
                      <img src="/skateboarding.svg" alt="Skate Park Icon" />
                    </button>
                  </Marker>
                ))}


              </ReactMapGL>
            </div>
            );


}



export default MapCustom;
