import React, { useState, useEffect } from "react";
import style from '../../assets/css/App.styles'

import ReactMapGL, { Marker, Popup } from "react-map-gl";


import useMyStore from "../../zustand"
import { Divider, Grid, Avatar } from "@material-ui/core";

import LocationCity from '@material-ui/icons/LocationCity';
import { GpacObj } from "../../zustand/types";

const [objStore] = useMyStore;
const MapCustom: React.FC<{}> = () => {
  const [viewport, setViewport] = useState({
    latitude: 45.4211,
    longitude: -75.6903,
    width: "100vw",
    height: "100vh",
    zoom: 10
  });

  const arraygpac = objStore(state => state.gpacMapObject);
  const getGpac = objStore(state => state.getGapMap);  
  const zipCodeArray = objStore(state => state.zipCode);
  const getZipcode = objStore(state => state.getZipCode);

  const [selectedPark, setSelectedPark] = useState<GpacObj | undefined>();
  useEffect(() => {
    const listener = (e: { key: string; }) => {
      if (e.key === "Escape") {
        setSelectedPark(undefined);
      }
    };
    (async function () {
      await getGpac();
      await getZipcode();
    })()
    window.addEventListener("keydown", listener);

    return () => {
      window.removeEventListener("keydown", listener);
    };
  }, []);

  return (
    <div>
      <ReactMapGL
        {...viewport}
        mapboxApiAccessToken="pk.eyJ1IjoiZ3VpbGxlcm1vYW5kYSIsImEiOiJja2JiNHVyMjYwMHlvMnNueGY3aDRlcG00In0.5JlHUpgSA1la6mjvYycTMQ"
        mapStyle='mapbox://styles/mapbox/streets-v11'
        onViewportChange={viewport2 => {
          setViewport({
            latitude: viewport2.altitude,
            longitude: viewport2.longitude,
            width: "100vw",
            height: "100vh",
            zoom: viewport2.zoom
          });
        }

        }
      >

        {arraygpac.map(park => (
          <Marker
            key={park.id}
            latitude={park.latitud}
            longitude={park.longitud}
          >
            <button
              className="marker-btn"
              onClick={e => {
                e.preventDefault();
                setSelectedPark(park);
              }}
            >
              <LocationCity />
            </button>
          </Marker>
        )
        )}
        {selectedPark ? (
          <Popup
            latitude={selectedPark.latitud}
            longitude={selectedPark.longitud}
            onClose={() => {
              setSelectedPark(undefined);
            }}
          >
            <div>

              <Grid container spacing={1}>
                <Grid item xs={12}>
                  <h5>{selectedPark.name}</h5>
                  <h5>Manager</h5>
                </Grid>
                <Grid item xs={3}>
                <Avatar >OP</Avatar>
                </Grid>
                <Grid item xs={9}>
                  <p>{selectedPark.zipCode}</p>
                </Grid>
                <Grid item xs={12}>
                 <button>Profile</button>
                </Grid>
              </Grid>


            </div>
          </Popup>
        ) : null}
      </ReactMapGL>
    </div>
  );


}



export default MapCustom;
