import React, { useState, useEffect } from "react";
import style from '../../assets/css/App.styles'  

import ReactMapGL, { Marker, Popup } from "react-map-gl";
import * as parkDate from './../../data/skateboard-park.json';
    const MapCustom :  React.FC<{}> = () => {
      const [viewport, setViewport] = useState({
        longitude: -75.3372987731628,
        latitude: 45.383321536272049,
        width: "100vw",
        height: "100vh",
        zoom: 10
      });

      type HomeProps = {
        id?: number | string;
        Name?: string;
    }


   type selected = {

       PARK_ID: Number,
       NAME: string,
       ADDRESS: string,
       ADDRESS_FR: string,
       DESCRIPTIO: string,
       lat: number ,
       lon:number


  };

    const [selectedPark, setSelectedPark] = useState<selected | null>(null);
      useEffect(() => {
        const listener = (e: { key: string; }) => {
          if (e.key === "Escape") {
            setSelectedPark(null);
          }
        };
        window.addEventListener("keydown", listener);

        return () => {
          window.removeEventListener("keydown", listener);
        };
      }, []);

            return (
              <div>
              <ReactMapGL
        {...viewport}
        mapboxApiAccessToken="pk.eyJ1IjoiZ3VpbGxlcm1vYW5kYSIsImEiOiJja2JiNHVyMjYwMHlvMnNueGY3aDRlcG00In0.5JlHUpgSA1la6mjvYycTMQ"
        mapStyle='mapbox://styles/mapbox/streets-v11'
        onViewportChange={ viewport2 =>{
           setViewport({
            latitude: viewport2.altitude,
            longitude: viewport2.longitude,
            width: "100vw",
            height: "100vh",
            zoom: viewport2.zoom
           });
        }

        }
      >

          <Marker
            key={1}
            latitude={-75.3372987731628}
            longitude={45.383321536272049}
          >
            <button
              className="marker-btn"
              onClick={e => {
                e.preventDefault();
                //xsetSelectedPark(park);
              }}
            >
              <img src="/skateboarding.svg" alt="Skate Park Icon" />
            </button>
          </Marker>


          <Popup
            latitude={-75.3372987731628}
            longitude={45.383321536272049}
            onClose={() => {
              setSelectedPark(null);
            }}
          >
            <div>
              <h2>test</h2>
              <p>opo</p>
            </div>
          </Popup>

      </ReactMapGL>
            </div>
            );


}



export default MapCustom;
